  const client = await pool.connect();
  try {
    const setClauses = [];
    const queryParams = [userId]; // First parameter is userId
    let paramIndex = 2; // Start index for profileData parameters

    for (const key in profileData) {
      if (Object.prototype.hasOwnProperty.call(profileData, key)) {
        setClauses.push(`${key} = $${paramIndex}`);
        queryParams.push(profileData[key]);
        paramIndex++;
      }
    }

    if (setClauses.length === 0) {
      console.log("No profile data provided for update.");
      return { success: false, message: "No profile data to update." };
    }

    const query = `
      UPDATE "Users"
      SET ${setClauses.join(', ')}
      WHERE id = $1
      RETURNING *;
    `;

    const result = await client.query(query, queryParams);

    if (result.rowCount > 0) {
      return { success: true, updatedUser: result.rows[0] };
    } else {
      return { success: false, message: "User not found or no changes made." };
    }
  } catch (error) {
    console.error("Error updating user profile:", error);
    return { success: false, error: error.message };
  } finally {
    client.release();
  }
}

